contracts/MainStructs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

abstract contract MainStructs {
    enum Networks {
        ETH,
        PLG,
        BSC,
        AVAX
    }

    enum NetworkRoles {
        SOURCE,
        DESTINATION
    }

    enum BoltActors {
        RELAYER,
        PROTOCOL,
        OPERATOR,
        WATCHER
    }

    enum SlashRules {
        RULE1,
        RULE2,
        RULE3
    }

    enum Status {
        INVALID,
        WAITING,
        FULFILLED,
        COMPLETED,
        REFUNDED
    }

    enum LockTypes {
        SWAP_LOCK,
        PENDING_REWARD,
        UNSTAKE
    }

    struct Route {
        Networks src;
        Networks dst;
    }

    // TODO: byte marshaling
    // TODO: move to executor
    struct ExecutableMessage {
        // uint routeId;
        address tokenIn;
        address callAddress;
        bytes callData; // includes amount, receiver etc.
        address tokenOut;
    }

    struct SwapData {
        uint nonce;
        address requester;
        address boltRelayerAddr;
        Route route;
        uint crossAmount;
        uint totalFees;
        // uint startBlock;
        ExecutableMessage srcMsg;
        ExecutableMessage dstMsg;
        Status status;
    }

    struct FulfillData {
        bytes32 swapID;
        uint startBlock;
        address boltOperator;
    }

    struct RelayData {
        bytes32 swapID;
        address boltOperator;
    }

    struct LockedFunds {
        uint amount;
        uint nonce;
        uint routeID;
        address token;
        address depositor;
        address receiver;
        bytes32 swapID;
        // uint until;
        // bytes32 h; // the swap ID this is locked for
        LockTypes lockType;
    }

    struct StakedFunds {
        mapping(address => uint) stakeByToken;
        mapping(address => uint) totalLockedByToken;
    }

    struct BoltRelayer {
        mapping(uint => StakedFunds) stakesByRoute;
        uint unstakeEnableBlock;
        bytes[] extraAddresses; // placeholder
    }
}

